dunes-script-module.script:
var listVMs=new Array();
sqlStatement="SELECT Hostname FROM deployment WHERE vRA_Status IN ('reserved','add','added');";
var main = new JDBCConnection();
var con;
try  {
	con = main.getConnection( JDBCString, DBUser, DBUserPassword );
	var stat = con.createStatement();
	var results = stat.executeQuery( sqlStatement );

	while(results.next()){
		listVMs.push(results.getStringAt(1));
	}
	stat.close();
}
catch( ex )  {
	throw "Connection to database failed (Reason: " + ex + ")";
} finally {
	if (con) {
		con.close();
	}
	return listVMs;
}
