dunes-script-module.script:
prettyName=prettyName.split(" (")[0];
sqlQuery="SELECT DISTINCT DNSSufix FROM Network WHERE prettyName='"+prettyName+"' ORDER BY DNSSufix;";
try  {
	var main = new JDBCConnection();
	con = main.getConnection( jdbcString, DBUser,DbUserPass  );
	var stat = con.createStatement();
	var results = stat.executeQuery(sqlQuery);
	//walk thought all rows
	var output = new Array();
	while ( results.next() ) {
		output.push(results.getStringAt(1));
	}
}
catch( ex )  {
	throw "Connection to database failed (Reason: " + ex + ")";
} finally {
	if (con) {
		con.close();
	}
	return output;
}
