dunes-script-module.script:
//requestTimeSQL=System.formatDate(requestTime,"yyyy-MM-dd HH:mm:ss")

sqlStatement="UPDATE deployment SET vCenter='"+vCenter+"',AKHManager_Status='new',vRA_Status='add',vRA_Timestamp=GETDATE() WHERE DeploymentID='"+deploymentId+"';";

var main = new JDBCConnection();
var con;
try  {
	con = main.getConnection( JDBCString, DBUser, DBUserPassword );
	//System.log( "Connection to database successful" );

	var stat = con.createStatement();
	var result = stat.execute( sqlStatement );
	stat.close();
	if ( result == 0 )  {
		return true;
	} else  {
		return false;
	}

}
catch( ex )  {
	throw "Connection to database failed (Reason: " + ex + ")";
} finally {
	if (con) {
		con.close();
	}
}
