dunes-script-module.script:
var tenant = System.getContext().getParameter("__asd_tenantRef");
var cafeHost = vCACCAFEHostManager.getDefaultHostForTenant(tenant , "true");
requestID=payload.machine.properties.get("__Cafe.Root.Request.Id");

var catResources = vCACCAFEEntitiesFinder.findCatalogResources(cafeHost);
for each (resource in catResources){
	//System.log(resource.resourceTypeRef.getLabel());
	//System.log( resource.getName());
    if ((resource.resourceTypeRef.getLabel() == "Deployment")&&(resource.requestId==requestID)){
    	System.log("*** DeploymentName :"+ resource.getName());
		return resource.getName();
      }
}
System.log("*** No deploymentName found");
return null;
