while (task != null) {

	var name = task.name != null ? "'" + task.name + "' " : "";
	var state = task.getState();
	if (state == "success") {
		System.log("Replication Task " + name + "succeeded");
		break;
	}
	else if (state == "error") {
		throw "Replication Task " + name + "error: " + task.getErrotMessage();
	}
	else if (state == "running") {
		if(task.getProgress() != null) {
			//System.log("Task " + name + task.getProgress() + "%");
		}
		else {
			//System.log("Task " + name + "in progress");
		}
	}
	else if (state == "queued") {
			//System.log("Replication Task " + name + "is queued");
	}

	System.sleep(500);
}

if (task == null) {
	throw "HMS Task is null";
}
