dunes-script-module.script:
var endpoint = VAPIManager.findEndpoint("https://"+ vc.id + "/api");
var client = endpoint.client();
var service = new com_vmware_cis_tagging_tag(client);
var list = service.list();

var tagsNames = new Array();
for each (var tagId in list) {
    tagsNames.push(service.get(tagId).name);  
}

return tagsNames;
