dunes-script-module.script:
System.log("Looking for Resource Element Category: " + categoryPath + " and element: " + elementName);
var category = Server.getResourceElementCategoryWithPath(categoryPath);

for (var i in category.resourceElements) {
	var resourceElement = category.resourceElements[i];
	if (resourceElement.name == elementName) {
		return resourceElement;
	}
}

System.error('Cannot find resource element at category [' + categoryPath + '] with name [' + elementName + ']');
return null;
