dunes-script-module.script:
return function() {
	this.isString = function(value){ 
		return ((typeof value === 'string') || (value instanceof String));
	};
	this.minString = function(value, min){ 
		return (this.isString(value) &&  value.length > min);
	};	
	this.notEmptyString = function(value){ 
		return this.minString(value, 1);
	};
	this.isIPv4 = function(value) { 
		return (!!value && 
			value.match(/^(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/)) 
			? true : false; // http://www.w3resource.com/javascript/form/ip-address-validation.php
	};
	this.regex = function(value, regex) {
		return (value.match(regex)) ? true : false;
	};
	this.isBlank = function(value) {
    	return (!value || /^\s*$/.test(value));
	};
};
