dunes-script-module.script:
var settings = new VRReplicationSettings();
settings.name = vm.name;
settings.rpo = rpo;
settings.quiesceGuestEnabled = quiesceGuestEnabled;
settings.networkCompressionEnabled = networkCompressionEnabled;

if (enableMPIT) {
	var retentionPolicyTier = new VRRetentionPolicyTier();
	retentionPolicyTier.granularityMinutes = Math.floor((24/instancesPerDay)*60);
	retentionPolicyTier.numSlots = numDays * instancesPerDay;
	settings.retentionPolicyTiers = [retentionPolicyTier];
}

System.log("###########################################################");
System.log(datastore);
System.log(datastore.id);
System.log(datastore.name);
System.log(datastore.morType);
System.log(datastore.path);
System.log(datastore.vcUriHost);
System.log("###########################################################");

return remoteSite.configureReplicationTo(vm.id, datastore.id, settings);
