dunes-script-module.script:
var configCategory = Server.getConfigurationElementCategoryWithPath(configCategoryPath);

if(configCategory != null) {
	var allElements = configCategory.configurationElements;
	var myResults = [];

	for (i=0; i<allElements.length; i++) {
		System.log("Configuration element found: " + allElements[i].name);
		myResults.push(allElements[i]);
	}

	return myResults;

} else {
	throw "Path not found!";
}
