dunes-script-module.script:
if (restHost == null || username == null || password == null || projectId == null) return null;

// Query service parameter

var parameters = "projectId=" + projectId;
var contentSources = System.getModule("com.vmware.vra.extensibility.rest.catalog").getContentSources(restHost,username,password, customHeaders, parameters);

var contentSourceIds = [];
for each (var contentSource in contentSources) {
    contentSourceIds.push(contentSource.id);
}

return contentSourceIds;
