dunes-script-module.script:
if (restHost == null || username == null || password == null || deploymentName == null) return null;

// Query service parameter
var parameters = "search=" + encodeURIComponent(deploymentName);
var deployments = System.getModule("com.vmware.vra.extensibility.rest.deployment").getDeployments(restHost,username,password, parameters);
if (deployments.length == 1) return deployments[0];
if (deployments.length == 0) return null;

// More projects returned than expected !
System.warn("getDeploymentIdByNameQS returned " +  deployments.length + " deployments");
return null;
