dunes-script-module.script:
// Basic version returning as many deployments as the default paging allows
// More advanced version is possible making multiple paged REST calls to get all deployments
// /deployment/api/projects/{projectId}/deployment-count

if (restHost == null || username == null || password == null) return null;

var url = "/deployment/api/deployments"
var contentObject = System.getModule("com.vmware.vra.extensibility.rest").getObjectFromUrl(restHost,username,password,null,url, parameters);
return contentObject.content;
