dunes-script-module.script:
// Must have restHost, url, custom headers or username and password)
if (restHost == null ||  url == null || (customHeaders == null && (username == null || password == null))) return null;

if ((customHeaders == null || customHeaders == undefined || customHeaders.keys.length == 0)) customHeaders = System.getModule("com.vmware.vra.extensibility").getvRA8CustomHeaders(restHost, username, password);
if (parameters != null && parameters != "") {
    url = url + "?" + parameters; // Parameters must be encoded
}

//System.debug(url);

try {
		var json = System.getModule("com.vmware.vra.extensibility").invokeRestOperation(restHost, "GET", url, "", customHeaders);
        var object = JSON.parse(json);
        //System.debug("Got JSON object : \n" + JSON.stringify(object,null,2));
        return object;
	} catch (e) {
        //throw "Unable to get object from url : " + url + "\n" + e + "\nWith Content : " + json;
		System.warn("Unable to get object from url : " + url + "\n" + e + "\nWith Content : " + json);
}
