function addUpdatePropertyFromVirtualMachineEntity(vCACHost, virtualMachineEntity, propertyName, propertyValue) {
    var propertyIsHidden = false;
    propertyIsRuntime = false;
    propertyIsEncrypted = false;
    doNotUpdate = false;
    System.getModule("com.vmware.library.vcac").addUpdatePropertyFromVirtualMachineEntity(vCACHost,virtualMachineEntity,propertyName,propertyValue,propertyIsHidden,propertyIsRuntime,propertyIsEncrypted,doNotUpdate);
}



vCACVm=Server.findAllForType("vCAC:VirtualMachine", "ExternalReferenceId eq '" + vcvm.id + "'")[0];
vCACHost= Server.findAllForType("vCAC:VCACHost")[0];
var vCACMachineEntity = vCACVm.getEntity();
var vCACMachineEntityProperties = vCACMachineEntity.getLink(vCACHost, "VirtualMachineProperties");
addUpdatePropertyFromVirtualMachineEntity(vCACHost, vCACMachineEntity,propertyName ,value);
