workflow-item.script:
//filter out unusabel signs and shorten in VM description and VM usage

//replace umlaute with long Umlaut
tr = {"ä":"ae", "ü":"ue", "ö":"oe", "ß":"ss", "Ä":"Ae","Ü":"Ue","Ö":"Oe"}
replaceUmlauts = function(s) {
    return s.replace(/[äöüßÄÖÜ]/g, function($0) { return tr[$0] })
}

//filter and cut fuction
function filterIt(eingabe){
	if ((eingabe==null)||(eingabe=="")){
		// following row added by HUH 30.01.2018
		eingabe="AKH Wien"
		return eingabe;
	}
	eingabe=replaceUmlauts(eingabe);
	regex=new RegExp("[a-zA-Z0-9\ ]","g");
	temp=eingabe.match(regex);
	//check if there is anything left in the sting
	if ((temp==null)||(temp=="")){
		return "";
	} else {
		ausgabe=temp.join(""); 
	 	//cuting string to 50 character
		return ausgabe.substring(0 ,50);
	}
}

outDescription=filterIt(description);
tempDescription=outDescription;
outUsage=filterIt(usage);

var inputProperties =  new Properties();
inputProperties.put("Notes",tempDescription);

vcacvm=Server.findAllForType("vCAC:VirtualMachine", "VirtualMachineName eq '" + vcacvMName + "'")[0];
entity=vcacvm.getEntity();
