workflow-item.script:
var myVcHttpNfcLease = snapshot.exportSnapshot();
var childSnapshots = snapshot.childSnapshot;

var myVcVirtualDeviceConfigSpec = new VcVirtualDeviceConfigSpec() ;
myVcVirtualDeviceConfigSpec.operation = VcVirtualDeviceConfigSpecOperation.edit;

var vm = snapshot.vm;
for each (var device in vm.config.hardware.device) {
	if (device instanceof VcVirtualDisk) {
		System.log( "Found virtual disk: " + device );
		myVcVirtualDeviceConfigSpec.device = device;
		if (device.backing instanceof VcVirtualDiskFlatVer2BackingInfo) {
			System.log( "Found path to parent" );
			//var myVcVirtualDiskFlatVer2BackingInfo = device.backing;
			//var newVcVirtualDiskFlatVer2BackingInfo = myVcVirtualDiskFlatVer2BackingInfo.parent.parent;
			//newVcVirtualDiskFlatVer2BackingInfo.fileName = "[NFS_DS_02] test3/2_test3.vmdk";
			//myVcVirtualDeviceConfigSpec.device.backing.parent.parent = newVcVirtualDiskFlatVer2BackingInfo;
			myVcVirtualDeviceConfigSpec.device.backing.parent.parent.fileName = "[NFS_DS_02] test3/2_test3.vmdk";
			myVcVirtualDeviceConfigSpec.device.backing.parent.parent.contentId = null;
		}
	}
}

var myVcVirtualDeviceConfigSpecArray = new Array();
myVcVirtualDeviceConfigSpecArray.push( myVcVirtualDeviceConfigSpec );
var myVcVirtualMachineConfigSpec = new VcVirtualMachineConfigSpec() ;
myVcVirtualMachineConfigSpec.deviceChange = myVcVirtualDeviceConfigSpecArray;
var task = vm.reconfigVM_Task(myVcVirtualMachineConfigSpec);
