workflow-item.script:
//vRO Scriptable task to parse a resource element and show values

//inputs:
//jsonTemplate : ResourceElement

//Get the content of the Resource Element
template = jsonTemplate.getContentAsMimeAttachment();

//Use JSON.parse to parse content in to a useable object
jsonObject = JSON.parse(template.content);

var ships = jsonObject.StarFleet["Ships"];
var shipProperties;
var shipMetrics;

for (ship in ships)
{
	System.log(ships[ship].Name);

	shipProperties = ships[ship].Properties;
	shipMetrics = ships[ship].Metrics;

	for (property in shipProperties)
	{
		System.log(ships[ship].Properties[property].Class);
		System.log(ships[ship].Properties[property].Registry);
		System.log(ships[ship].Properties[property].Captain);
	}

	for (metric in shipMetrics)
	{
		System.log(ships[ship].Metrics[metric].CurrentSpeed);
		System.log(ships[ship].Metrics[metric].ShieldStatus);
	}
}
