var MS_PER_MINUTE = 60000;
var durationInMinutes = 20;
var myEndTime = (new Date()).getTime();
var myStartTime = new Date(myEndTime - durationInMinutes * MS_PER_MINUTE).getTime();

latestStats.values[0].resourceId = iD_01;
latestStats.values[0]["stat-list"].stat[0].timestamps = [myStartTime, myEndTime];
latestStats.values[0]["stat-list"].stat[0].statKey.key = "cpu|costop_summation";

var params = [iD_01];	
System.log("Adding properties... " + JSON.stringify(latestStats));

